static const char *skipLookup(BoxedString *p, uint32_t idx, int packed) {
    if (idx > p->skip.length)
        return NULL;
    auto ent = idx / PXT_STRING_SKIP_INCR;
    auto data = packed ? PXT_SKIP_DATA_PACK(p) : PXT_SKIP_DATA_IND(p);
    auto size = p->skip.size;
    if (ent) {
        auto off = packed ? p->skip_pack.list[ent - 1] : p->skip.list[ent - 1];
        data += off;
        size -= off;
        idx &= PXT_STRING_SKIP_INCR - 1;
    }
    return utf8Skip(data, size, idx);
}

extern LLSegment workQueue;

static uint32_t fixSize(BoxedString *p, uint32_t *len) {
    uint32_t tlen = 0;
    uint32_t sz = 0;
    if (workQueue.getLength())
        oops(81);
    workQueue.push((TValue)p);
    while (workQueue.getLength()) {
        p = (BoxedString *)workQueue.pop();
        if (IS_CONS(p)) {
            workQueue.push((TValue)p->cons.right);
            workQueue.push((TValue)p->cons.left);
        } else {
            tlen += p->getLength();
            sz += p->getUTF8Size();
        }
    }
    *len = tlen;
    return sz;
}

static void fixCopy(BoxedString *p, char *dst) {
    if (workQueue.getLength())
        oops(81);

    workQueue.push((TValue)p);
    while (workQueue.getLength()) {
        p = (BoxedString *)workQueue.pop();
        if (IS_CONS(p)) {
            workQueue.push((TValue)p->cons.right);
            workQueue.push((TValue)p->cons.left);
        } else {
            auto sz = p->getUTF8Size();
            memcpy(dst, p->getUTF8Data(), sz);
            dst += sz;
        }
    }
}

// switches CONS representation into skip list representation
// does not switch representation of CONS' children
static void fixCons(BoxedString *r) {
    uint32_t length = 0;
    auto sz = fixSize(r, &length);
    auto numSkips = length / PXT_STRING_SKIP_INCR;
    // allocate first, while [r] still holds references to its children
    // because allocation might trigger GC
    auto data = (uint16_t *)gcAllocateArray(numSkips * 2 + sz + 1);
    // copy, while [r] is still cons
    fixCopy(r, (char *)(data + numSkips));
    // now, set [r] up properly
    r->vtable = &string_skiplist16_vt;
    r->skip.size = sz;
    r->skip.length = length;
    r->skip.list = data;
    setupSkipList(r, NULL, 0);
}
#endif

STRING_VT(string_inline_ascii, NOOP, NOOP, 2 + p->ascii.length + 1, p->ascii.data, p->ascii.length,
          p->ascii.length, idx <= p->ascii.length ? p->ascii.data + idx : NULL)
#if PXT_UTF8
STRING_VT(string_inline_utf8, NOOP, NOOP, 2 + p->utf8.size + 1, p->utf8.data, p->utf8.size,
          utf8Len(p->utf8.data, p->utf8.size), utf8Skip(p->utf8.data, p->utf8.size, idx))
STRING_VT(string_skiplist16, NOOP, if (p->skip.list) gcMarkArray(p->skip.list), 2 * sizeof(void *),
          PXT_SKIP_DATA_IND(p), p->skip.size, p->skip.length, skipLookup(p, idx, 0))
STRING_VT(string_skiplist16_packed, NOOP, NOOP,
          2 + 2 + PXT_NUM_SKIP_ENTRIES(p) * 2 + p->skip.size + 1, PXT_SKIP_DATA_PACK(p),
          p->skip.size, p->skip.length, skipLookup(p, idx, 1))
STRING_VT(string_cons, fixCons(p), (gcScan((TValue)p->cons.left), gcScan((TValue)p->cons.right)),
          2 * sizeof(void *), PXT_SKIP_DATA_IND(p), p->skip.size, p->skip.length,
          skipLookup(p, idx, 0))
#endif

PRIM_VTABLE(number, ValType::Number, BoxedNumber, 0)
PRIM_VTABLE(buffer, ValType::Object, BoxedBuffer, p->length)
// PRIM_VTABLE(action, ValType::Function, RefAction, )

void failedCast(TValue v, void *addr) {
    DMESG("failed type check for %p @%p", v, addr);
    auto vt = getAnyVTable(v);
    if (vt) {
        DMESG("VT %p - objtype %d classNo %d", vt, vt->objectType, vt->classNo);
    }

    int code;
    if (v == TAG_NULL)
        code = PANIC_CAST_FROM_NULL;
    else
        code = PANIC_CAST_FIRST + (int)valType(v);
    soft_panic(code);
}

void missingProperty(TValue v) {
    DMESG("missing property on %p", v);
    soft_panic(PANIC_MISSING_PROPERTY);
}

#ifdef PXT_PROFILE
struct PerfCounter *perfCounters;

struct PerfCounterInfo {
    uint32_t numPerfCounters;
    char *perfCounterNames[0];
};

#define PERF_INFO ((PerfCounterInfo *)(((uintptr_t *)bytecode)[13]))

void initPerfCounters() {
    auto n = PERF_INFO->numPerfCounters;
    perfCounters = new PerfCounter[n];
    memset(perfCounters, 0, n * sizeof(PerfCounter));
}

void dumpPerfCounters() {
    auto info = PERF_INFO;
    DMESG("calls,us,name");
    for (uint32_t i = 0; i < info->numPerfCounters; ++i) {
        auto c = &perfCounters[i];
        DMESG("%d,%d,%s", c->numstops, c->value, info->perfCounterNames[i]);
    }
}

void startPerfCounter(PerfCounters n) {
    if (!perfCounters)
        return;
    auto c = &perfCounters[(uint32_t)n];
    if (c->start)
        oops(50);
    c->start = PERF_NOW();
}

void stopPerfCounter(PerfCounters n) {
    if (!perfCounters)
        return;
    auto c = &perfCounters[(uint32_t)n];
    if (!c->start)
        oops(51);
    c->value += PERF_NOW() - c->start;
    c->start = 0;
    c->numstops++;
}
#endif

// Exceptions

#ifndef PXT_EXN_CTX
#define PXT_EXN_CTX() getThreadContext()
#endif

typedef void (*RestoreStateType)(TryFrame *, ThreadContext *);
#ifndef pxt_restore_exception_state
#define pxt_restore_exception_state ((RestoreStateType)(((uintptr_t *)bytecode)[14]))
#endif

//%
TryFrame *beginTry() {
    auto ctx = PXT_EXN_CTX();
    auto frame = (TryFrame *)app_alloc(sizeof(TryFrame));
    frame->parent = ctx->tryFrame;
    ctx->tryFrame = frame;
    return frame;
}

//% expose
void endTry() {
    auto ctx = PXT_EXN_CTX();
    auto f = ctx->tryFrame;
    if (!f)
        oops(51);
    ctx->tryFrame = f->parent;
    app_free(f);
}

//% expose
void throwValue(TValue v) {
    auto ctx = PXT_EXN_CTX();
    auto f = ctx->tryFrame;
    if (!f) {
        DMESG("unhandled exception, value:");
        anyPrint(v);
        soft_panic(PANIC_UNHANDLED_EXCEPTION);
    }
    ctx->tryFrame = f->parent;
    TryFrame copy = *f;
    app_free(f);
    ctx->thrownValue = v;
    pxt_restore_exception_state(&copy, ctx);
}

//% expose
TValue getThrownValue() {
    auto ctx = PXT_EXN_CTX();
    auto v = ctx->thrownValue;
    ctx->thrownValue = TAG_NON_VALUE;
    if (v == TAG_NON_VALUE)
        oops(51);
    return v;
}

//% expose
void endFinally() {
    auto ctx = PXT_EXN_CTX();
    if (ctx->thrownValue == TAG_NON_VALUE)
        return;
    throwValue(getThrownValue());
}

// https://en.wikipedia.org/wiki/Fowler%E2%80%93Noll%E2%80%93Vo_hash_function
uint32_t hash_fnv1(const void *data, unsigned len) {
    const uint8_t *d = (const uint8_t *)data;
    uint32_t h = 0x811c9dc5;
    while (len--)
        h = (h * 0x1000193) ^ *d++;
    return h;
}

// redefined in melody.cpp
__attribute__((weak)) int redirectSamples(int16_t *dst, int numsamples, int samplerate) {
    return 0;
}

} // namespace pxt

base.eventcontext.ts:
namespace control {
    /**
     * Run code when a registered event happens.
     * @param id the event compoent id
     * @param value the event value to match
     */
    //% weight=20 blockGap=8 blockId="control_on_event" block="on event|from %src|with value %value"
    //% blockExternalInputs=1
    //% help="control/on-event"
    export function onEvent(src: number, value: number, handler: () => void, flags = 16) { // EVENT_LISTENER_DEFAULT_FLAGS
        const ctx = control.eventContext();
        if (!ctx)
            control.internalOnEvent(src, value, handler, flags);
        else
            ctx.registerHandler(src, value, handler, flags);
    }

    export class FrameCallback {
        order: number
        handler: () => void
    }

    class EventHandler {
        constructor(
            public src: number,
            public value: number,
            public handler: () => void,
            public flags: number
        ) { }

        register() {
            control.internalOnEvent(this.src, this.value, () => {
                if (this.handler) this.handler();
            }, this.flags)
        }

        unregister() {
            control.internalOnEvent(this.src, this.value, doNothing, this.flags);
        }
    }

    function doNothing() { }



    export class EventContext {
        private handlers: EventHandler[];
        private frameCallbacks: FrameCallback[];
        private frameWorker: number;
        private framesInSample: number;
        private timeInSample: number;
        public deltaTimeMillis: number;
        private prevTimeMillis: number;
        private idleCallbacks: (() => void)[];

        static lastStats: string;
        static onStats: (stats: string) => void;

        constructor() {
            this.handlers = [];
            this.framesInSample = 0;
            this.timeInSample = 0;
            this.deltaTimeMillis = 0;
            this.frameWorker = 0;
            this.idleCallbacks = undefined;
            if (!EventContext.lastStats) {
                EventContext.lastStats = "";
            }
        }

        get deltaTime() {
            return this.deltaTimeMillis / 1000;
        }

        private runCallbacks() {
            control.enablePerfCounter("all frame callbacks")

            let loopStart = control.millis()
            this.deltaTimeMillis = loopStart - this.prevTimeMillis;
            this.prevTimeMillis = loopStart;
            for (let f of this.frameCallbacks) {
                f.handler()
            }
            let runtime = control.millis() - loopStart
            this.timeInSample += runtime
            this.framesInSample++
            if (this.timeInSample > 1000 || this.framesInSample > 30) {
                const fps = this.framesInSample / (this.timeInSample / 1000);
                EventContext.lastStats = `fps:${Math.round(fps)}`;
                if (fps < 99)
                    EventContext.lastStats += "." + (Math.round(fps * 10) % 10)
                if (control.ramSize() > 2000000 && control.profilingEnabled()) {
                    control.dmesg(`${(fps * 100) | 0}/100 fps - ${this.framesInSample} frames`)
                    control.gc()
                    control.dmesgPerfCounters()
                }
                this.timeInSample = 0
                this.framesInSample = 0
            }
            let delay = Math.max(1, 20 - runtime)

            return delay
        }

        private runningCallbacks: boolean;
        private registerFrameCallbacks() {
            if (!this.frameCallbacks) return;

            const worker = this.frameWorker;
            control.runInParallel(() => {
                if (this.runningCallbacks) {
                    // this context is still running in a different fiber;
                    // delay until the other fiber doing so has ceased.
                    pauseUntil(() => !this.runningCallbacks);
                }
                this.runningCallbacks = true;

                this.framesInSample = 0;
                this.timeInSample = 0;
                this.deltaTimeMillis = 0;
                this.prevTimeMillis = control.millis();

                while (worker == this.frameWorker) {
                    let delay = this.runCallbacks()
                    pause(delay)
                }

                this.runningCallbacks = false;
            })
        }

        register() {
            for (const h of this.handlers)
                h.register();
            this.registerFrameCallbacks();
        }

        unregister() {
            for (const h of this.handlers)
                h.unregister();
            this.frameWorker++;
        }

        registerFrameHandler(order: number, handler: () => void): FrameCallback {
            if (!this.frameCallbacks) {
                this.frameCallbacks = [];
                this.registerFrameCallbacks();
            }

            const fn = new FrameCallback()
            fn.order = order
            fn.handler = handler
            for (let i = 0; i < this.frameCallbacks.length; ++i) {
                if (this.frameCallbacks[i].order > order) {
                    this.frameCallbacks.insertAt(i, fn)
                    return fn;
                }
            }
            this.frameCallbacks.push(fn);
            return fn;
        }

        unregisterFrameHandler(fn: FrameCallback) {
            if (!fn || !this.frameCallbacks) return;
            const i = this.frameCallbacks.indexOf(fn);
            if (i > -1)
                this.frameCallbacks.splice(i, 1);
        }

        registerHandler(src: number, value: number, handler: () => void, flags: number) {
            // already there?
            for (const h of this.handlers) {
                if (h.src == src && h.value == value) {
                    h.flags = flags;
                    h.handler = handler;
                    return;
                }
            }
            // register and push
            const hn = new EventHandler(src, value, handler, flags);
            this.handlers.push(hn);
            hn.register();
        }

        addIdleHandler(handler: () => void) {
            if (!this.idleCallbacks) {
                this.idleCallbacks = [];
                this.registerHandler(15/*DAL.DEVICE_ID_SCHEDULER*/, 2/*DAL.DEVICE_SCHEDULER_EVT_IDLE*/, () => this.runIdleHandler(), 16);
            }
            this.idleCallbacks.push(handler);
        }

        removeIdleHandler(handler: () => void) {
            if (handler && this.idleCallbacks)
                this.idleCallbacks.removeElement(handler);
        }

        private runIdleHandler() {
            if (this.idleCallbacks) {
                const ics = this.idleCallbacks.slice(0);
                ics.forEach(ic => ic());
            }
        }
    }
    let eventContexts: EventContext[];

    /**
     * Gets the current event context if any
     */
    export function eventContext(): EventContext {
        return eventContexts ? eventContexts[eventContexts.length - 1] : undefined;
    }

    /**
     * Pushes a new event context and clears all handlers
     */
    export function pushEventContext(): EventContext {
        if (!eventContexts)
            eventContexts = [];

        // unregister previous context
        const ctx = eventContext();
        if (ctx) ctx.unregister();
        // register again
        const n = new EventContext();
        eventContexts.push(n);
        return n;
    }

    /**
     * Pops the current event context and restore handlers if any previous context
     */
    export function popEventContext() {
        if (!eventContexts) return;

        // clear current context
        const ctx = eventContexts.pop();
        if (!ctx) return;
        ctx.unregister();

        // register old context again
        const context = eventContexts[eventContexts.length - 1];
        if (context)
            context.register();
        else
            eventContexts = undefined;
    }

    let _idleCallbacks: (() => void)[];
    /**
     * Registers a function to run when the device is idling
     * @param handler
    */
    export function onIdle(handler: () => void) {
        if (!handler) return;

        const ctx = eventContext();
        if (ctx) ctx.addIdleHandler(handler);
        else {
            if (!_idleCallbacks) {
                _idleCallbacks = [];
                control.runInBackground(function () {
                    while (_idleCallbacks) {
                        _idleCallbacks.slice(0).forEach(cb => cb());
                        pause(20);
                    }
                })
                /*
                control.internalOnEvent(
                    15. // DAL.DEVICE_ID_SCHEDULER
                    2, // DAL.DEVICE_SCHEDULER_EVT_IDLE
                    function() {
                        pins.LED.digitalWrite(on = !on);
                        if (_idleCallbacks)
                            _idleCallbacks.slice(0).forEach(cb => cb());
                    }, 192); // MESSAGE_BUS_LISTENER_IMMEDIATE
                */
            }
            _idleCallbacks.push(handler);
        }
    }

    export function removeIdleHandler(handler: () => void) {
        if (!handler) return;
        const ctx = eventContext();
        if (ctx) ctx.removeIdleHandler(handler);
        else if (_idleCallbacks) _idleCallbacks.removeElement(handler);
    }
}

