static inline VTable *getVTable(RefObject *r) {
    return (VTable *)(r->vt() & ~1);
}

static inline VTable *getAnyVTable(TValue v) {
    if (!isRefCounted(v))
        return NULL;
    auto vt = getVTable((RefObject *)v);
    if (vt->magic == VTABLE_MAGIC)
        return vt;
    return NULL;
}

