// Melodies from file microbitmusictunes.c https://github.com/bbcmicrobit/MicroPython

enum Sounds {
    //% block="power up"
    PowerUp,
    //% block="power down"
    PowerDown,
    //% block="jump up"
    JumpUp,
    //% block="jump down"
    JumpDown,
    //% block="ba ding"
    BaDing,
    //% block="wawawawaa"
    Wawawawaa,
    //% block="magic wand"
    MagicWand,
    //% block="siren"
    Siren
}

namespace music {
    let _soundQueue: control.AnimationQueue;

    /**
     * Get the melody string for a built-in melody.
     * @param name the note name, eg: Note.C
     */
    //% help=music/sounds
    //% blockId=music_sounds block="%name"
    //% blockHidden=true
    //% name.fieldEditor="gridpicker"
    //% name.fieldOptions.width=285
    //% name.fieldOptions.columns=3
    export function sounds(name: Sounds): string {
        switch (name) {
            case Sounds.BaDing:
                return 'b5:1 e6:3';
            case Sounds.Wawawawaa:
                return 'e3:3 r:1 d#:3 r:1 d:4 r:1 c#:8';
            case Sounds.JumpUp:
                return 'c5:1 d e f g';
            case Sounds.JumpDown:
                return 'g5:1 f e d c';
            case Sounds.PowerUp:
                return 'g4:1 c5 e g:2 e:1 g:3';
            case Sounds.PowerDown:
                return 'g5:1 d# c g4:2 b:1 c5:3';
            case Sounds.MagicWand:
                return 'F#6:1-300 G# A# B C7# D# F F# G# A# B:6'; //A#7:1-200 A:1 A#7:1 A:1 A#7:2
            case Sounds.Siren:
                return 'a4 d5 a4 d5 a4 d5';
            default:
                return '';
        }
    }

    function soundQueue(): control.AnimationQueue {
        if (!_soundQueue) _soundQueue = new control.AnimationQueue();
        return _soundQueue;
    }

    /**
     * Start playing a sound and don't wait for it to finish.
     * Notes are expressed as a string of characters with this format: NOTE[octave][:duration]
     * @param sound the melody to play
     */
    //% help=music/play-sound
    //% blockId=music_play_sound block="play sound %sound=music_sounds"
    //% parts="headphone"
    //% weight=95 blockGap=8
	//% group="Sound"
    export function playSound(sound: string) {
        const queue = soundQueue();
        const melody = new Melody(sound);
        control.runInParallel(() => {
            queue.cancel();
            queue.runUntilDone(() => melody.playNextNote());
        })
        pause(1);
    }


    /**
     * Play a sound and wait until the sound is done.
     * Notes are expressed as a string of characters with this format: NOTE[octave][:duration]
     * @param sound the melody to play
     */
    //% help=music/play-sound-until-done
    //% blockId=music_play_sound_until_done block="play sound %sound=music_sounds|until done"
    //% parts="headphone"
    //% weight=94 blockGap=8
	//% group="Sound"
    export function playSoundUntilDone(sound: string) {
        const queue = soundQueue();
        const melody = new Melody(sound);
        queue.runUntilDone(() => melody.playNextNote());
    }

    /**
     * Stop all sounds from playing.
     */
    //% help=music/stop-all-sounds
    //% blockId=music_stop_all_sounds block="stop all sounds"
    //% weight=93
	//% group="Volume"
    export function stopAllSounds() {
        const queue = soundQueue();
        queue.cancel();
        music.rest(1);
    }	

    class Melody {
        static freqTable: number[];
        notes: string;
        duration: number;
        octave: number;
        pos: number;
        tempo: number;

        constructor(notes: string) {
            this.notes = notes;
            this.duration = 4; //Default duration (Crotchet)
            this.octave = 4; //Middle octave
            this.pos = 0;
            this.tempo = 120; // default tempo
            // TODO: use HEX literal
            if (!Melody.freqTable)
                Melody.freqTable = [31, 33, 35, 37, 39, 41, 44, 46, 49, 52, 55, 58, 62, 65, 69, 73, 78, 82, 87, 92, 98, 104, 110, 117, 123, 131, 139, 147, 156, 165, 175, 185, 196, 208, 220, 233, 247, 262, 277, 294, 311, 330, 349, 370, 392, 415, 440, 466, 494, 523, 554, 587, 622, 659, 698, 740, 784, 831, 880, 932, 988, 1047, 1109, 1175, 1245, 1319, 1397, 1480, 1568, 1661, 1760, 1865, 1976, 2093, 2217, 2349, 2489, 2637, 2794, 2960, 3136, 3322, 3520, 3729, 3951, 4186, 4435, 4699, 4978, 5274, 5588, 5920, 6272, 6645, 7040, 7459, 7902]
        }

        hasNextNote() {
            return this.pos < this.notes.length;
        }

        scanNextNote(): string {
            // eat space
            while (this.pos < this.notes.length) {
                const c = this.notes[this.pos];
                if (c != ' ' && c != '\r' && c != '\n' && c != '\t')
                    break;
                this.pos++;
            }

            // read note
            let note = "";
            while (this.pos < this.notes.length) {
                const c = this.notes[this.pos];
                if (c == ' ' || c == '\r' || c == '\n' || c == '\t')
                    break;
                note += c;
                this.pos++;
            }
            return note;
        }

        playNextNote(): boolean {
            let currNote = this.scanNextNote();
            if (!currNote)
                return false;

            enum Token {
                Note = 0,
                Octave = 1,
                Beat = 2,
                Tempo = 3
            }

            let note: number = 0;
            let isrest: boolean = false;
            let token: string = "";
            let tokenKind = Token.Note;
            const consumeToken = () => {
                if (token && tokenKind != Token.Note) {
                    const d = parseInt(token);
                    switch (tokenKind) {
                        case Token.Octave: this.octave = d; break;
                        case Token.Beat: this.duration = Math.max(1, Math.min(16, d)); break;
                        case Token.Tempo: this.tempo = Math.max(1, d); break;
                    }
                    token = "";
                }
            }
            for (let i = 0; i < currNote.length; i++) {
                let noteChar = currNote.charAt(i);
                switch (noteChar) {
                    case 'c': case 'C': note = 1; break;
                    case 'd': case 'D': note = 3; break;
                    case 'e': case 'E': note = 5; break;
                    case 'f': case 'F': note = 6; break;
                    case 'g': case 'G': note = 8; break;
                    case 'a': case 'A': note = 10; break;
                    case 'b': case 'B': note = 12; break;
                    case 'r': case 'R': isrest = true; break;
                    case '#': note++; break;
                    case 'b': note--; break;
                    case ':':
                        consumeToken();
                        tokenKind = Token.Beat;
                        break;
                    case '-':
                        consumeToken();
                        tokenKind = Token.Tempo;
                        break;
                    default:
                        if(tokenKind == Token.Note)
                            tokenKind = Token.Octave;
                        token += noteChar;
                        break;
                }
            }
            consumeToken();
            let beat = 15000 / this.tempo;
            if (isrest) {
                music.rest(this.duration * beat)
            } else {
                const keyNumber = note + (12 * (this.octave - 1));
                const frequency = Melody.freqTable[keyNumber] || 0;
                music.playTone(frequency, this.duration * beat);
            }

            return this.hasNextNote();
        }
    }
}

music.music.cpp:
#include "pxt.h"

// There is no DAC on STM32F401

#define NOTE_PAUSE 20

enum class SoundOutputDestination {
  //% block="pin PWM"
  PinPWM = 1,
  //% block="speaker"
  Speaker = 0,
};

static int Scale(double value, int originalMin, int originalMax, int scaleMin, int scaleMax) {
	auto scale = (double)(scaleMax - scaleMin) / (originalMax - originalMin);
	auto ret = (int)(scaleMin + ((value - originalMin) * scale));

	return ret > scaleMax ? scaleMax : (ret < scaleMin ? scaleMin : ret);
}

namespace music {

SoundOutputDestination soundOutputDestination = SoundOutputDestination::Speaker;
int vol = 127;

/**
 * Turn the on-board speaker on or off.
 * @param out the destination for sounds generated by the synthesizer
 */
// weight=2
// blockId=music_set_output block="set output %out"
// parts="speaker" blockGap=8 advanced=true
void setOutput(SoundOutputDestination out) {
  if (out != soundOutputDestination) {
    soundOutputDestination = out;
  }
}

/**
* Play a tone through the speaker for some amount of time.
* @param frequency pitch of the tone to play in Hertz (Hz), eg: Note.C
* @param ms tone duration in milliseconds (ms), eg: BeatFraction.Whole
*/
//% help=music/play-tone
//% blockId=music_play_note block="play|tone %note=device_note|for %duration=device_beat"
//% parts="headphone" async
//% blockNamespace=music
//% weight=76 blockGap=8
//% group="Tone"
void playTone(int frequency, int ms) {
  auto pitchPin = lookupPin(
      soundOutputDestination == SoundOutputDestination::Speaker ? PB_8 : PA_5);

  if (frequency <= 0) {
    pitchPin->setAnalogValue(0);
  } else {
    //pitchPin->setAnalogValue(512);
	auto v = Scale(vol, 0, 255, 0, 1024);
	pitchPin->setAnalogValue(v);
    pitchPin->setAnalogPeriodUs(1000000 / frequency);
    if (ms > 0) {
      int d = max(1, ms - NOTE_PAUSE); // allow for short rest
      int r = max(1, ms - d);
      fiber_sleep(d);
      pitchPin->setAnalogValue(0);
      fiber_sleep(r);
    }
  }
}

/**
 * Set the default output volume of the sound synthesizer.
 * @param volume the volume 0...255
 */
//% blockId=synth_set_volume block="set volume %volume"
//% volume.min=0 volume.max=255
//% volume.defl=127
//% help=music/set-volume
//% weight=70
//% group="Volume"
//% blockGap=8
void setVolume(int volume) {
	auto pitchPin = lookupPin(soundOutputDestination == SoundOutputDestination::Speaker ? PB_8 : PA_5);
	
	auto v = Scale(volume, 0, 255, 0, 512);
	pitchPin->setAnalogValue(v);
	
	vol = volume;

} 

/**
 * Returns the current output volume of the sound synthesizer.
 */
//% blockId=synth_get_volume block="volume"
//% help=music/volume
//% weight=69
//% group="Volume"
//% blockGap=8
int volume() {
	return vol;
}

}

