#define BRAINPAD_TICK_LED10 PC_14
#define BRAINPAD_TICK_LED11 PB_12
#define BRAINPAD_TICK_LED12 PB_2
#define BRAINPAD_TICK_LED13 PH_3
#define BRAINPAD_TICK_LED14 PC_15
