    cfgData = *(int **)(PXT_BOOTLOADER_CFG_ADDR);
#ifdef PXT_BOOTLOADER_CFG_MAGIC
    cfgData++;
    if ((void*)0x200 <= cfgData && cfgData < (void*)PXT_BOOTLOADER_CFG_ADDR &&
        cfgData[-1] == (int)PXT_BOOTLOADER_CFG_MAGIC)
#endif
        for (int i = 0;; i += 2) {
            if (cfgData[i] == key)
                return cfgData[i + 1];
            if (cfgData[i] == 0)
                break;
        }
