// A base abstract class for ref-counted objects.
class RefObject {
  public:
    uint16_t refcnt;
    uint16_t vtable;

    RefObject(uint16_t vt) {
        refcnt = 3;
        vtable = vt;
    }

    void destroyVT();
    void printVT();

    inline bool isReadOnly() { return refcnt == 0xffff; }

    // Increment/decrement the ref-count. Decrementing to zero deletes the current object.
    inline void ref() {
        if (isReadOnly())
            return;
        MEMDBG("INCR: %p refs=%d", this, this->refcnt);
        check(refcnt > 1, ERR_REF_DELETED);
        refcnt += 2;
    }

    inline void unref() {
        if (isReadOnly())
            return;
        MEMDBG("DECR: %p refs=%d", this, this->refcnt);
        check(refcnt > 1, ERR_REF_DELETED);
        check((refcnt & 1), ERR_REF_DELETED);
        refcnt -= 2;
        if (refcnt == 1) {
            destroyVT();
        }
    }
};

