                #define ORDER(A,B) A(0); B(4); A(8); B(12); A(16); B(20); A(24); B(28)
                //#define ORDER(A,B) for (int k = 0; k < 32; k += 8) { A(k); B(4+k); }
                #define LOOP(A,B,xbot) while (cnt--) { \
                            auto v = *fdata++; \
