                    else \
                        LOOP(STEPA, STEPB, xbot)

                if (color >= 0) {
                #define SETHIGH(s) *tdata = (*tdata & 0x0f) | ((COLS(s)) << 4)
                #define SETLOW(s) *tdata = (*tdata & 0xf0) | COLS(s)
                    LOOPS(sh)
