namespace sprites {
    enum ObstacleFlags {
        Moved = 1 << 4,
        Dead = 1 << 5
    }

    export interface Obstacle {
        x: number;
        y: number;
        left: number;
        right: number;
        top: number;
        bottom: number;
        width: number;
        height: number;
        layer: number;
        image: Image;
        tileIndex: number;
    }

    export class StaticObstacle implements Obstacle {
        layer: number;
        image: Image;
        tileIndex: number;

        top: number;
        left: number;

        constructor(image: Image, top: number, left: number, layer: number, tileIndex?: number) {
            this.image = image;
            this.layer = layer;
            this.top = top;
            this.left = left;
            this.tileIndex = tileIndex;
        }

        get x(): number {
            return this.left + this.width >> 1;
        }

        get y(): number {
            return this.top + this.height >> 1;
        }

        get height(): number {
            return this.image.height;
        }

        get width(): number {
            return this.image.width;
        }

        get bottom(): number {
            return this.top + this.height;
        }

        get right(): number {
            return this.left + this.width;
        }
    }
}

game.physics.ts:
class PhysicsEngine {
    constructor() {
    }

    /**
     * Adds sprite to the physics
     * @param sprite
     */
    addSprite(sprite: Sprite) { }

    removeSprite(sprite: Sprite) { }

    moveSprite(s: Sprite, tm: tiles.TileMap, dx: number, dy: number) { }

    draw() { }

    /** Apply physics */
    move(dt: number) { }

    /**
     * Apply collisions
     */
    collisions() { }

    overlaps(sprite: Sprite): Sprite[] { return []; }
}

const MAX_DISTANCE = 15; // pixels
const MAX_TIME_STEP = 0.1; // seconds
const MAX_VELOCITY = MAX_DISTANCE / MAX_TIME_STEP;
const GAP = 0.1;

/**
 * A physics engine that does simple AABB bounding box check
 */
class ArcadePhysicsEngine extends PhysicsEngine {
    protected sprites: Sprite[];
    protected map: sprites.SpriteMap;

    constructor() {
        super();
        this.sprites = [];
    }

    addSprite(sprite: Sprite) {
        this.sprites.push(sprite);
    }

    removeSprite(sprite: Sprite) {
        this.sprites.removeElement(sprite);
    }

    draw() {
        if (this.map)
            this.map.draw();
    }

    move(dt: number) {
        dt = Math.min(MAX_TIME_STEP, dt);
        const dt2 = dt / 2;

        const tm = game.currentScene().tileMap;

        for (let s of this.sprites) {
            const ovx = constrain(s.vx);
            const ovy = constrain(s.vy);

            s.vx = constrain(s.vx + s.ax * dt)
            s.vy = constrain(s.vy + s.ay * dt)

            this.moveSprite(s, tm, (s.vx + ovx) * dt2, (s.vy + ovy) * dt2);
        }
    }

    collisions() {
        // 1: clear obstacles
        for (let i = 0; i < this.sprites.length; ++i)
            this.sprites[i].clearObstacles();

        // 2: refresh non-ghost collision map
        const colliders = this.sprites.filter(sprite => !(sprite.flags & sprites.Flag.Ghost));

        if (colliders.length < 10) {
            // not enough sprite, just brute force it
            this.map = undefined;
        } else {
            if (!this.map) this.map = new sprites.SpriteMap();
            this.map.update(colliders);
        }

        // 3: go through sprite and handle collisions
        const scene = game.currentScene();
        const tm = scene.tileMap;

        for (const sprite of colliders) {
            const overSprites = scene.physicsEngine.overlaps(sprite);
            for (const overlapper of overSprites) {
                // overlap handler
                const tmpsprite = sprite;
                const tmp = overlapper;
                const oh = sprite.overlapHandler;
                if (oh)
                    control.runInParallel(() => oh(tmp))
                scene.overlapHandlers
                    .filter(h => h.type == sprite.type && h.otherType == overlapper.type)
                    .forEach(h => control.runInParallel(() => h.handler(tmpsprite, tmp)));
            }

            const xDiff = sprite.x - sprite._lastX;
            const yDiff = sprite.y - sprite._lastY;
            if ((xDiff !== 0 || yDiff !== 0) && Math.abs(xDiff) < MAX_DISTANCE && Math.abs(yDiff) < MAX_DISTANCE) {
                // Undo the move
                sprite.x = sprite._lastX;
                sprite.y = sprite._lastY;

                // Now move it with the tilemap in mind
                this.moveSprite(sprite, tm, xDiff, yDiff);
            }
        }
    }

    /**
     * Returns sprites that overlap with the given sprite. If type is non-zero, also filter by type.
     * @param sprite
     * @param layer
     */
    overlaps(sprite: Sprite): Sprite[] {
        if (this.map)
            return this.map.overlaps(sprite);
        else {
            // brute force
            const layer = sprite.layer;
            const r: Sprite[] = [];
            const n = this.sprites.length;
            for (let i = 0; i < n; ++i) {
                if ((layer & this.sprites[i].layer)
                    && sprite.overlapsWith(this.sprites[i]))
                    r.push(this.sprites[i]);
            }
            return r;
        }
    }

    public moveSprite(s: Sprite, tm: tiles.TileMap, dx: number, dy: number) {
        if (dx === 0 && dy === 0) {
            s._lastX = s.x;
            s._lastY = s.y;
            return;
        }

        if (tm && !(s.flags & sprites.Flag.Ghost)) {
            s._hitboxes.forEach(box => {
                const t0 = box.top >> 4;
                const r0 = box.right >> 4;
                const b0 = box.bottom >> 4;
                const l0 = box.left >> 4;

                if (dx > 0) {
                    let topCollide = tm.isObstacle(r0 + 1, t0);
                    if (topCollide || tm.isObstacle(r0 + 1, b0)) {
                        const nextRight = box.right + dx;
                        const maxRight = ((r0 + 1) << 4) - GAP
                        if (nextRight > maxRight) {
                            dx -= (nextRight - maxRight);
                            s.registerObstacle(CollisionDirection.Right, tm.getObstacle(r0 + 1, topCollide ? t0 : b0))
                        }
                    }
                }
                else if (dx < 0) {
                    const topCollide = tm.isObstacle(l0 - 1, t0);
                    if (topCollide || tm.isObstacle(l0 - 1, b0)) {
                        const nextLeft = box.left + dx;
                        const minLeft = (l0 << 4) + GAP;
                        if (nextLeft < minLeft) {
                            dx -= (nextLeft - minLeft);
                            s.registerObstacle(CollisionDirection.Left, tm.getObstacle(l0 - 1, topCollide ? t0 : b0))
                        }
                    }
                }

                if (dy > 0) {
                    const rightCollide = tm.isObstacle(r0, b0 + 1);
                    if (rightCollide || tm.isObstacle(l0, b0 + 1)) {
                        const nextBottom = box.bottom + dy;
                        const maxBottom = ((b0 + 1) << 4) - GAP;
                        if (nextBottom > maxBottom) {
                            dy -= (nextBottom - maxBottom);
                            s.registerObstacle(CollisionDirection.Bottom, tm.getObstacle(rightCollide ? r0 : l0, b0 + 1))
                        }
                    }
                }
                else if (dy < 0) {
                    const rightCollide = tm.isObstacle(r0, t0 - 1);
                    if (tm.isObstacle(r0, t0 - 1) || tm.isObstacle(l0, t0 - 1)) {
                        const nextTop = box.top + dy;
                        const minTop = (t0 << 4) + GAP;
                        if (nextTop < minTop) {
                            dy -= (nextTop - minTop);
                            s.registerObstacle(CollisionDirection.Top, tm.getObstacle(rightCollide ? r0 : l0, t0 - 1))
                        }
                    }
                }

                // Now check each corner and bump out if necessary. This step is needed for
                // the case where a hitbox goes diagonally into the corner of a tile.
                const t1 = (box.top + dy) >> 4;
                const r1 = (box.right + dx) >> 4;
                const b1 = (box.bottom + dy) >> 4;
                const l1 = (box.left + dx) >> 4;

                if (tm.isObstacle(r1, t1)) {
                    // bump left
                    dx -= (box.right + dx - ((r1 << 4) - GAP))
                    s.registerObstacle(CollisionDirection.Right, tm.getObstacle(r1, t1));
                }
                else if (tm.isObstacle(l1, t1)) {
                    // bump right
                    dx -= (box.left + dx - (((l1 + 1) << 4) + GAP));
                    s.registerObstacle(CollisionDirection.Left, tm.getObstacle(l1, t1));
                }
                else {
                    const rightCollide = tm.isObstacle(r1, b1);
                    if (rightCollide || tm.isObstacle(l1, b1)) {
                        // bump up because that is usually better for platformers
                        dy -= (box.bottom + dy - ((b1 << 4) - GAP));
                        s.registerObstacle(CollisionDirection.Bottom, tm.getObstacle(rightCollide ? r1 : l1, b1));
                    }
                }
            });
        }

        s.x += dx;
        s.y += dy;
        s._lastX = s.x;
        s._lastY = s.y;
    }
}

function constrain(v: number) {
    return Math.abs(v) > MAX_VELOCITY ? Math.sign(v) * MAX_VELOCITY : v;
}

game.scene.ts:
/**
 * Control the background, tiles and camera
 */
//% groups='["Background", "Tiles", "Camera"]'
namespace scene {
    export enum Flag {
        NeedsSorting = 1 << 1,
    }

    export interface SpriteHandler {
        type: number;
        handler: (sprite: Sprite) => void;
    }

    export interface OverlapHandler {
        type: number;
        otherType: number;
        handler: (sprite: Sprite, otherSprite: Sprite) => void;
    }

    export interface CollisionHandler {
        type: number;
        tile: number;
        handler: (sprite: Sprite) => void
    }

    export class Scene {
        eventContext: control.EventContext;
        background: Background;
        tileMap: tiles.TileMap;
        allSprites: SpriteLike[];
        spritesByKind: Sprite[][];
        physicsEngine: PhysicsEngine;
        camera: scene.Camera;
        flags: number;
        destroyedHandlers: SpriteHandler[];
        createdHandlers: SpriteHandler[];
        overlapHandlers: OverlapHandler[];
        collisionHandlers: CollisionHandler[];

        constructor(eventContext: control.EventContext) {
            this.eventContext = eventContext;
            this.flags = 0;
            this.physicsEngine = new ArcadePhysicsEngine();
            this.camera = new scene.Camera();
            this.background = new Background(this.camera);
            this.destroyedHandlers = [];
            this.createdHandlers = [];
            this.overlapHandlers = [];
            this.collisionHandlers = [];
            this.spritesByKind = [];
        }

        init() {
            if (this.allSprites) return;

            this.allSprites = [];
            scene.setBackgroundColor(0)
            // update controller state
            this.eventContext.registerFrameHandler(8, () => {
                performance.startTimer("controller_update")
                const dt = this.eventContext.deltaTime;
                controller.__update(dt);
                performance.stopTimer("controller_update")
            })
            // update sprites in tilemap
            this.eventContext.registerFrameHandler(9, () => {
                if (this.tileMap) {
                    performance.startTimer("tilemap_update")
                    this.tileMap.update(this.camera);
                    performance.stopTimer("tilemap_update")
                }
            })
            // apply physics 10
            this.eventContext.registerFrameHandler(10, () => {
                performance.startTimer("physics")
                const dt = this.eventContext.deltaTime;
                this.physicsEngine.move(dt);
                performance.stopTimer("physics")
            })
            // user update 20
            // apply collisions 30
            this.eventContext.registerFrameHandler(30, () => {
                performance.startTimer("collisions")
                const dt = this.eventContext.deltaTime;
                this.physicsEngine.collisions();
                this.camera.update();
                for (const s of this.allSprites)
                    s.__update(this.camera, dt);
                performance.stopTimer("collisions")
            })
            // render background 60
            this.eventContext.registerFrameHandler(60, () => {
                this.background.render();
            })
            // paint 75
            // render sprites 90
            this.eventContext.registerFrameHandler(90, () => {
                if (this.flags & Flag.NeedsSorting)
                this.allSprites.sort(function (a, b) { return a.z - b.z || a.id - b.id; })
                performance.startTimer("sprite_draw")
                for (const s of this.allSprites)
                    s.__draw(this.camera);
                performance.stopTimer("sprite_draw")
            })
            // render diagnostics
            this.eventContext.registerFrameHandler(150, () => {
                if (game.debug)
                    this.physicsEngine.draw();
                // clear flags
                this.flags = 0;
            });
            // update screen
            this.eventContext.registerFrameHandler(200, control.__screen.update);
        }
    }
}

game.scenes.ts:
/**
 * Control the background, tiles and camera
 */
//% weight=47 color="#401255" icon="\uf1bb"
//% groups='["Screen", "Tiles", "Collisions", "Camera"]'
//% blockGap=8
namespace scene {
    /**
     * Get the width of the screen in pixels
     */
    //% blockId=scenescreenwidth block="screen width"
    //% group="Screen"
    //% weight=100 blockGap=8
    //% help=scene/screen-width
    export function screenWidth(): number {
        return screen.width;
    }

    /**
     * Gets the height of the screen in pixels
     */
    //% blockId=scenescreenheight block="screen height"
    //% group="Screen"
    //% weight=99
    //% help=scene/screen-width
    export function screenHeight(): number {
        return screen.height;
    }

    /**
     * Set the game background color
     * @param color
     */
    //% group="Screen"
    //% weight=25
    //% blockId=gamesetbackgroundcolor block="set background color to %color=colorindexpicker"
    //% help=scene/set-background-color
    export function setBackgroundColor(color: number) {
        const scene = game.currentScene();
        scene.background.color = color;
    }

    /**
     * Get the game background color
     * @param color
     */
    //% group="Screen"
    //% weight=22
    //% blockId=gamebackgroundcolor block="background color"
    //% help=scene/background-color
    export function backgroundColor() : number {
        const scene = game.currentScene();
        return scene.background.color;
    }

    /**
     * Set a picture as the background
     */
    //% group="Screen"
    //% weight=24
    //% blockId=gamesetbackgroundimage block="set background image to %img=background_image_picker"
    //% help=scene/set-background-image
    export function setBackgroundImage(img: Image) {
        const scene = game.currentScene();
        scene.background.image = img;
    }

    /**
     * Get the current background image
     */
    //% weight=22
    //% group="Screen"
    //% blockId=gamebackgroundimage block="background image"
    //% help=scene/background-image
    export function backgroundImage(): Image {
        const scene = game.currentScene();
        return scene.background.image;
    }

    /**
     * Adds a moving background layer
     * @param distance distance of the layer which determines how fast it moves, eg: 10
     * @param img
     */
    //% group="Screen"
    //% weight=10
    export function addBackgroundLayer(image: Image, distance?: number, alignment?: BackgroundAlignment) {
        const scene = game.currentScene();
        if (image)
            scene.background.addLayer(image, distance || 100, alignment || BackgroundAlignment.Bottom);
    }

    /**
     * Set the map for placing tiles in the scene
     * @param map
     */
    //% blockId=gamesettilemap block="set tile map to %map=tilemap_image_picker"
    //% group="Tiles"
    //% help=scene/set-tile-map
    export function setTileMap(map: Image) {
        const scene = game.currentScene();
        if (!scene.tileMap)
            scene.tileMap = new tiles.TileMap();
        scene.tileMap.setMap(map);
    }

    /**
     * Set a tile at the given index
     * @param tile
     * @param index
     */
    //% blockId=gamesettileat block="set %tile=gamegettile to %index=colorindexpicker"
    //% group="Tiles"
    //% weight=30
    //% help=scene/set-tile-at
    export function setTileAt(tile: tiles.Tile, index: number) {
        const scene = game.currentScene();
        if (!scene.tileMap)
            scene.tileMap = new tiles.TileMap();
        scene.tileMap.setTileAt(tile.x >> 4, tile.y >> 4, index);
    }

    /**
     * Set an image as a tile at the given index. Tiles should be a 16x16 image
     * @param index
     * @param img
     */
    //% blockId=gamesettile block="set tile %index=colorindexpicker to %img=screen_image_picker||with wall %wall=toggleOnOff"
    //% group="Tiles"
    //% help=scene/set-tile
    export function setTile(index: number, img: Image, wall?: boolean) {
        const scene = game.currentScene();
        if (!scene.tileMap)
            scene.tileMap = new tiles.TileMap();
        scene.tileMap.setTile(index, img, !!wall);
    }

    /**
     * Get the tile at a position in the tile map
     * @param col
     * @param row
     */
    //% blockId=gamegettile block="tile col %col row %row"
    //% group="Tiles" blockSetVariable="myTile"
    //% help=scene/get-tile
    export function getTile(col: number, row: number): tiles.Tile {
        const scene = game.currentScene();
        if (!scene.tileMap)
            scene.tileMap = new tiles.TileMap();
        return scene.tileMap.getTile(col, row);
    }

    /**
     * Get all tiles in the tile map with the given index.
     * @param index
     */
    //% blockId=gamegettilestype block="array of all %index=colorindexpicker tiles"
    //% group="Tiles" blockSetVariable="tile list"
    //% help=scene/get-tiles-by-type
    export function getTilesByType(index: number): tiles.Tile[] {
        const scene = game.currentScene();
        if (!scene.tileMap)
            scene.tileMap = new tiles.TileMap();
        return scene.tileMap.getTilesByType(index);
    }

    /**
     * Set the game camera to follow a sprite
     * @param sprite
     */
    //% blockId=camerafollow block="camera follow sprite %sprite=variables_get(mySprite)"
    //% group="Camera"
    //% help=scene/camera-follow-sprite
    export function cameraFollowSprite(sprite: Sprite) {
        const scene = game.currentScene();
        scene.camera.sprite = sprite;
    }

    /**
     * Moves the camera center to a coordinate position
     * @param sprite
     */
    //% blockId=camerapos block="center camera at x %x y %y"
    //% group="Camera"
    //% help=scene/center-camera-at
    export function centerCameraAt(x: number, y: number) {
        const scene = game.currentScene();
        scene.camera.sprite = undefined;
        scene.camera.offsetX = x - (screen.width >> 1);
        scene.camera.offsetY = y - (screen.height >> 1);
    }
}

game.sprite.ts:
enum SpriteFlag {
    //% block="ghost"
    Ghost = sprites.Flag.Ghost,
    //% block="auto destroy"
    AutoDestroy = sprites.Flag.AutoDestroy,
    //% block="stay in screen"
    StayInScreen = sprites.Flag.StayInScreen
}

enum CollisionDirection {
    //% block="left"
    Left = 0,
    //% block="top"
    Top = 1,
    //% block="right"
    Right = 2,
    //% block="bottom"
    Bottom = 3
}

interface SpriteLike {
    z: number;
    id: number;
    __update(camera: scene.Camera, dt: number): void;
    __draw(camera: scene.Camera): void;
}

enum FlipOption {
    //% block=none
    None,
    //% block="flip x"
    FlipX,
    //% block="flip y"
    FlipY,
    //% block="flip x+y"
    FlipXY
}

/**
 * A sprite on the screen
 **/
//% blockNamespace=sprites color="#4B7BEC" blockGap=8 
class Sprite implements SpriteLike {
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="x (horizontal position)"
    x: number
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="y (vertical position)"
    y: number
    private _z: number
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="vx (velocity x)"
    vx: number
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="vy (velocity y)"
    vy: number
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="ax (acceleration x)"
    ax: number
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="ay (acceleration y)"
    ay: number

    _type: number;

    /**
     * A bitset of layer. Each bit is a layer, default is 1.
     */
    //% group="Properties"
    layer: number;

    _lastX: number;
    _lastY: number;

    _action: number; //Used with animation library

    /**
     * Time to live in game ticks. The lifespan decreases by 1 on each game update
     * and the sprite gets destroyed when it reaches 0.
     */
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="lifespan"
    lifespan: number;
    private _image: Image;
    private _obstacles: sprites.Obstacle[];

    private updateSay: (dt: number, camera: scene.Camera) => void;
    private sayBubbleSprite: Sprite;

    _hitboxes: game.Hitbox[];

    flags: number
    id: number

    overlapHandler: (other: Sprite) => void;
    collisionHandlers: (() => void)[][];
    private destroyHandler: () => void;

    constructor(img: Image) {
        this.x = screen.width >> 1;
        this.y = screen.height >> 1;
        this._z = 0
        this._lastX = this.x;
        this._lastY = this.y;
        this.vx = 0
        this.vy = 0
        this.ax = 0
        this.ay = 0
        this.flags = 0
        this.setImage(img);
        this.type = -1; // not a member of any type by default
        this.layer = 1; // by default, in layer 1
        this.lifespan = undefined;
    }

    /**
     * Gets the current image
     */
    //% group="Lifecycle"
    //% blockId=spriteimage block="%sprite(mySprite) image"
    //% weight=8
    get image(): Image {
        return this._image;
    }

    /**
     * Sets the image on the sprite
     */
    //% group="Lifecycle"
    //% blockId=spritesetimage block="set %sprite(mySprite) image to %img=screen_image_picker"
    //% weight=7 help=sprites/sprite/set-image
    setImage(img: Image) {
        if (!img) return; // don't break the sprite

        // Identify old upper left corner
        let oMinX = img.width;
        let oMinY = img.height;
        let oMaxX = 0;
        let oMaxY = 0;

        for (let i = 0; this._hitboxes && i < this._hitboxes.length; ++i) {
            let box = this._hitboxes[i];
            oMinX = Math.min(oMinX, box.ox);
            oMinY = Math.min(oMinY, box.oy);
            oMaxX = Math.max(oMaxX, box.ox + box.width - 1);
            oMaxY = Math.max(oMaxY, box.oy + box.height - 1);
        }

        this._image = img;
        this._hitboxes = game.calculateHitBoxes(this);

        // Identify new upper left corner
        let nMinX = img.width;
        let nMinY = img.height;
        let nMaxX = 0;
        let nMaxY = 0;

        for (let i = 0; i < this._hitboxes.length; ++i) {
            let box = this._hitboxes[i];
            nMinX = Math.min(nMinX, box.ox);
            nMinY = Math.min(nMinY, box.oy);
            nMaxX = Math.max(nMaxX, box.ox + box.width - 1);
            nMaxY = Math.max(nMaxY, box.oy + box.height - 1);
        }

        const minXDiff = oMinX - nMinX;
        const minYDiff = oMinY - nMinY;
        const maxXDiff = oMaxX - nMaxX;
        const maxYDiff = oMaxY - nMaxY;

        const scene = game.currentScene();
        const tmap = scene.tileMap;

        if (scene.tileMap && this.width <= 16 && this.height <= 16) {
            const l = (nMinX + this.left) >> 4;
            const r = (nMaxX + this.left) >> 4;
            const t = (nMinY + this.top) >> 4;
            const b = (nMaxY + this.top) >> 4;

            if (tmap.isObstacle(l, t) && (minXDiff > 0 || minYDiff > 0)) {
                scene.physicsEngine.moveSprite(this, scene.tileMap, minXDiff, minYDiff);
            } else if (tmap.isObstacle(r, t) && (maxXDiff < 0 || minYDiff > 0)) {
                scene.physicsEngine.moveSprite(this, scene.tileMap, maxXDiff, minYDiff);
            } else if (tmap.isObstacle(l, b) && (minXDiff > 0 || maxYDiff < 0)) {
                scene.physicsEngine.moveSprite(this, scene.tileMap, minXDiff, maxYDiff);
            } else if (tmap.isObstacle(r, b) && (maxXDiff < 0 || maxYDiff < 0)) {
                scene.physicsEngine.moveSprite(this, scene.tileMap, maxXDiff, maxYDiff);
            }
        }
    }

    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="z (depth)"
    get z(): number {
        return this._z;
    }

    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="z (depth)"
    set z(value: number) {
        if (value != this._z) {
            this._z = value;
            game.currentScene().flags |= scene.Flag.NeedsSorting;
        }
    }

    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="width"
    get width() {
        return this._image.width
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="height"
    get height() {
        return this._image.height
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="left"
    get left() {
        return this.x - (this.width >> 1)
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="left"
    set left(value: number) {
        this.x = value + (this.width >> 1);
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="right"
    get right() {
        return this.left + this.width
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="right"
    set right(value: number) {
        this.x = value - (this.width >> 1);
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine
    get top() {
        return this.y - (this.height >> 1)
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine
    set top(value: number) {
        this.y = value + (this.height >> 1);
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="bottom"
    get bottom() {
        return this.top + this.height
    }
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="bottom"
    set bottom(value: number) {
        this.y = value - (this.height >> 1);
    }
    /**
     * The type of sprite
     */
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="kind"
    get type() {
        return this._type;
    }
    /**
     * The type of sprite
     */
    //% group="Properties" blockSetVariable="mySprite"
    //% blockCombine block="kind"
    set type(value: number) {
        if (value == undefined || this._type === value) return;

        const spritesByKind = game.currentScene().spritesByKind;
        if (this._type >= 0 && spritesByKind[this._type])
            spritesByKind[this._type].removeElement(this);

        if (value >= 0) {
            if (!spritesByKind[value]) spritesByKind[value] = [];
            spritesByKind[value].push(this);
        }

        this._type = value;
    }

    /**
     * Set the sprite position
     * @param x horizontal position
     * @param y vertical position
     */
    //% group="Properties"
    //% weight=100
    //% blockId=spritesetpos block="set %sprite(mySprite) position to x %x y %y"
    //% help=sprites/sprite/set-position
    setPosition(x: number, y: number): void {
        this.x = x;
        this.y = y;
    }

    /**
     * Display a speech bubble with the text, for the given time
     * @param text the text to say, eg: ":)"
     * @param time time to keep text on
     */
    //% group="Properties"
    //% blockId=spritesay block="%sprite(mySprite) say %text||for %millis ms"
    //% inlineInputMode=inline
    //% help=sprites/sprite/say
    say(text: string, timeOnScreen?: number, textColor = 15, textBoxColor = 1) {

        if (!text) {
            this.updateSay = undefined;
            if (this.sayBubbleSprite) {
                this.sayBubbleSprite.destroy();
            }
            return;
        }

        

        let pixelsOffset = 0;
        let holdTextSeconds = 1.5;
        let bubblePadding = 4;
        let maxTextWidth = 100;
        let font = image.font8;
        let startX = 2;
        let startY = 2;
        let bubbleWidth = text.length * font.charWidth + bubblePadding;
        let maxOffset = text.length * font.charWidth - maxTextWidth;
        let bubbleOffset: number;
        // sets the defaut scroll speed in pixels per second
        let speed = 45;

        // Calculates the speed of the scroll if scrolling is needed and a time is specified
        if (timeOnScreen && maxOffset > 0) {
            speed = (maxOffset + (2 * maxTextWidth)) / (timeOnScreen / 1000);
            speed = Math.max(speed, 45);
            holdTextSeconds = maxTextWidth / speed;
            holdTextSeconds = Math.min(holdTextSeconds, 1.5);
        } 

        if (timeOnScreen) {
            timeOnScreen = timeOnScreen + control.millis();
        }

        if (!this._hitboxes || this._hitboxes.length == 0) {
            bubbleOffset = 0;
        } else {
            bubbleOffset = this._hitboxes[0].top;
            for (let i = 0; i < this._hitboxes.length; i++) {
                bubbleOffset = Math.min(bubbleOffset, this._hitboxes[i].top);
            }

            // Gets the length from sprites location to its highest hitbox
            bubbleOffset = this.y - bubbleOffset;
        }


        if (bubbleWidth > maxTextWidth + bubblePadding) {
            bubbleWidth = maxTextWidth + bubblePadding;
        } else {
            maxOffset = -1;
        }

        // Destroy previous sayBubbleSprite to prevent leaking
        if (this.sayBubbleSprite) {
            this.sayBubbleSprite.destroy();
        }

        this.sayBubbleSprite = sprites.create(image.create(bubbleWidth, font.charHeight + bubblePadding), -1);

        this.sayBubbleSprite.setFlag(SpriteFlag.Ghost, true);
        this.updateSay = (dt, camera) => {
            // Update box stuff as long as timeOnScreen doesn't exist or it can still be on the screen
            if (!timeOnScreen || timeOnScreen > control.millis()) {
                this.sayBubbleSprite.image.fill(textBoxColor);
                // The minus 2 is how much transparent padding there is under the sayBubbleSprite
                this.sayBubbleSprite.y = this.y - bubbleOffset - ((font.charHeight + bubblePadding) >> 1) - 2;
                this.sayBubbleSprite.x = this.x;

                if (!this.isOutOfScreen(camera)) {
                    const ox = camera.offsetX;
                    const oy = camera.offsetY;

                    if (this.sayBubbleSprite.left - ox < 0) {
                        this.sayBubbleSprite.left = 0;
                    }

                    if (this.sayBubbleSprite.right - ox > screen.width) {
                        this.sayBubbleSprite.right = screen.width;
                    }

                    // If sprite bubble above the sprite gets cut off on top, place the bubble below the sprite
                    if (this.sayBubbleSprite.top - oy < 0) {
                        this.sayBubbleSprite.y = (this.sayBubbleSprite.y - 2 * this.y) * -1;
                    }
                }

                // Pauses at beginning of text for holdTextSeconds length
                if (holdTextSeconds > 0) {
                    holdTextSeconds -= game.eventContext().deltaTime;
                    // If scrolling has reached the end, start back at the beginning
                    if (holdTextSeconds <= 0 && pixelsOffset > 0) {
                        pixelsOffset = 0;
                        holdTextSeconds = maxTextWidth / speed;
                    }
                } else {
                    pixelsOffset += dt * speed;

                    // Pause at end of text for holdTextSeconds length
                    if (pixelsOffset >= maxOffset) {
                        pixelsOffset = maxOffset;
                        holdTextSeconds = maxTextWidth / speed;
                    }
                }
                // If maxOffset is negative it won't scroll
                if (maxOffset < 0) {
                    this.sayBubbleSprite.image.print(text, startX, startY, textColor, font);
                } else {
                    this.sayBubbleSprite.image.print(text, startX - pixelsOffset, startY, textColor, font);
                }

                // Left side padding
                this.sayBubbleSprite.image.fillRect(0, 0, bubblePadding >> 1, font.charHeight + bubblePadding, textBoxColor);
                // Right side padding
                this.sayBubbleSprite.image.fillRect(bubbleWidth - (bubblePadding >> 1), 0, bubblePadding >> 1, font.charHeight + bubblePadding, textBoxColor);
                // Corners removed
                this.sayBubbleSprite.image.setPixel(0, 0, 0);
                this.sayBubbleSprite.image.setPixel(bubbleWidth - 1, 0, 0);
                this.sayBubbleSprite.image.setPixel(0, font.charHeight + bubblePadding - 1, 0);
                this.sayBubbleSprite.image.setPixel(bubbleWidth - 1, font.charHeight + bubblePadding - 1, 0);
            } else {
                // If can't update because of timeOnScreen then destroy the sayBubbleSprite and reset updateSay
                this.sayBubbleSprite.destroy();
                this.updateSay = undefined;
            }
        }
    }

    /**
     * Indicates if the sprite is outside the screen
     */
    //%
    isOutOfScreen(camera: scene.Camera): boolean {
        const ox = camera.offsetX;
        const oy = camera.offsetY;
        return this.right - ox < 0 || this.bottom - oy < 0 || this.left - ox > screen.width || this.top - oy > screen.height;
    }

    __draw(camera: scene.Camera) {
        if (this.isOutOfScreen(camera)) return;

        const l = this.left - camera.offsetX;
        const t = this.top - camera.offsetY;
        const font = image.font8;
        screen.drawTransparentImage(this._image, l, t)

        // debug info
        if (game.debug) {
            let color = 1;
            this._hitboxes.forEach(box => {
                this._image.drawRect(box.ox, box.oy, box.width, box.height, color);
                color++;
                if (color >= 15) color = 1;
            });
        }
    }

    __update(camera: scene.Camera, dt: number) {
        if (this.lifespan !== undefined) {
            this.lifespan -= dt * 1000;
            if (this.lifespan <= 0) {
                this.lifespan = undefined;
                this.destroy();
            }
        }
        if ((this.flags & sprites.Flag.AutoDestroy)
            && this.isOutOfScreen(camera)) {
            this.destroy()
        }

        if (this.flags & sprites.Flag.StayInScreen) {
            if (this.left < camera.offsetX) {
                this.left = camera.offsetX;
            }
            else if (this.right > camera.offsetX + screen.width) {
                this.right = camera.offsetX + screen.width;
            }

            if (this.top < camera.offsetY) {
                this.top = camera.offsetY;
            }
            else if (this.bottom > camera.offsetY + screen.height) {
                this.bottom = camera.offsetY + screen.height;
            }
        }
        // Say text
        if (this.updateSay) {
            this.updateSay(dt, camera);
        }
    }

    /**
     * Set a sprite flag
     */
    //% group="Properties"
    //% blockId=spritesetsetflag block="set %sprite(mySprite) %flag %on=toggleOnOff"
    //% flag.defl=SpriteFlag.StayInScreen
    //% help=sprites/sprite/set-flag
    setFlag(flag: SpriteFlag, on: boolean) {
        if (on) this.flags |= flag
        else this.flags = ~(~this.flags | flag);
    }

    /**
     * Check if this sprite overlaps another sprite
     * @param other
     */
    //% group="Overlaps"
    //% blockId=spriteoverlapswith block="%sprite(mySprite) overlaps with %other=variables_get(otherSprite)"
    //% help=sprites/sprite/overlaps-with
    overlapsWith(other: Sprite) {
        if (other == this) return false;
        if (this.flags & sprites.Flag.Ghost)
            return false
        if (other.flags & sprites.Flag.Ghost)
            return false
        return other._image.overlapsWith(this._image, this.left - other.left, this.top - other.top)
    }

    /**
     * Registers code when the sprite overlaps with another sprite
     * @param spriteType sprite type to match
     * @param handler
     */
    //% group="Overlaps"
    //% afterOnStart=true
    //% help=sprites/sprite/on-overlap
    onOverlap(handler: (other: Sprite) => void) {
        this.overlapHandler = handler;
    }

    /**
     * Registers code when the sprite collides with an obstacle
     * @param direction
     * @param handler
     */
    //% blockNamespace="scene" group="Collisions"
    onCollision(direction: CollisionDirection, tileIndex: number, handler: () => void) {
        if (!this.collisionHandlers)
            this.collisionHandlers = [];

        direction = Math.max(0, Math.min(3, direction | 0));

        if (!this.collisionHandlers[direction])
            this.collisionHandlers[direction] = [];

        this.collisionHandlers[direction][tileIndex] = handler;
    }

    /**
     * Check if there is an obstacle in the given direction
     * @param direction
     */
    //% blockId=spritehasobstacle block="is %sprite(mySprite) hitting wall %direction"
    //% blockNamespace="scene" group="Collisions"
    //% help=sprites/sprite/is-hitting-tile
    isHittingTile(direction: CollisionDirection): boolean {
        return this._obstacles && !!this._obstacles[direction];
    }

    /**
     * Get the obstacle sprite in a given direction if any
     * @param direction
     */
    //% blockId=spriteobstacle block="%sprite(mySprite) wall hit on %direction"
    //% blockNamespace="scene" group="Collisions"
    //% help=sprites/sprite/tile-hit-from
    tileHitFrom(direction: CollisionDirection): number {
        return (this._obstacles && this._obstacles[direction]) ? this._obstacles[direction].tileIndex : -1;
    }

    clearObstacles() {
        this._obstacles = undefined;
    }

    registerObstacle(direction: CollisionDirection, other: sprites.Obstacle) {
        if (other == undefined) return;
        if (!this._obstacles)
            this._obstacles = [];
        this._obstacles[direction] = other;

        const handler = (this.collisionHandlers && this.collisionHandlers[direction]) ? this.collisionHandlers[direction][other.tileIndex] : undefined;
        if (handler)
            control.runInParallel(handler);
        const scene = game.currentScene();
        scene.collisionHandlers
            .filter(h => h.type == this.type && h.tile == other.tileIndex)
            .forEach(h => control.runInParallel(() => h.handler(this)));
    }

    /**
     * Run code when the sprite is destroyed
     * @param handler
     */
    //% group="Lifecycle"
    //% weight=9
    onDestroyed(handler: () => void) {
        this.destroyHandler = handler
    }

    /**
     * Destroy the sprite
     */
    //% group="Lifecycle"
    //% weight=10
    //% blockId=spritedestroy block="destroy %sprite(mySprite)"
    //% help=sprites/sprite/destroy
    destroy() {
        if (this.flags & sprites.Flag.Destroyed)
            return
        this.flags |= sprites.Flag.Destroyed
        const scene = game.currentScene();
        // When current sprite is destroyed, destroys sayBubbleSprite if defined
        if (this.sayBubbleSprite) {
            this.sayBubbleSprite.destroy();
        }
        scene.allSprites.removeElement(this);
        if (this.type >= 0 && scene.spritesByKind[this.type])
            scene.spritesByKind[this.type].removeElement(this);
        scene.physicsEngine.removeSprite(this);
        if (this.destroyHandler)
            this.destroyHandler();
        scene.destroyedHandlers
            .filter(h => h.type == this.type)
            .forEach(h => h.handler(this));
    }

    toString() {
        return `${this.id}(${this.x},${this.y})->(${this.vx},${this.vy})`;
    }
}

game.spriteevents.ts:
namespace sprites {
    /**
     * Gets the sprite type
     */
    //% blockHidden=1 shim=ENUM_GET
    //% blockId=spritetype block="$kind" enumInitialMembers="Player,Enemy"
    //% enumName=SpriteKind enumMemberName=kind enumPromptHint="e.g. Coin, Fireball, Asteroid..."
    export function _spriteType(kind: number): number {
        return kind;
    }

    /**
     * Run code when a certain kind of sprite is created
     * @param kind
     * @param sprite
     */
    //% group="Lifecycle" draggableParameters weight=97
    //% blockId=spritesoncreated block="on created $sprite of kind $kind=spritetype"
    //% help=sprites/on-created
    export function onCreated(kind: number, handler: (sprite: Sprite) => void): void {
        if (!handler || kind == undefined) return;

        const scene = game.currentScene();
        scene.createdHandlers.push({
            type: kind,
            handler: handler
        })
    }

    /**
     * Run code when a certain kind of sprite is destroyed
     * @param kind
     * @param sprite
     */
    //% group="Lifecycle"
    //% weight=96 draggableParameters
    //% blockId=spritesondestroyed block="on destroyed $sprite of kind $kind=spritetype "
    //% help=sprites/on-destroyed
    export function onDestroyed(kind: number, handler: (sprite: Sprite) => void) {
        if (!handler || kind == undefined) return;

        const scene = game.currentScene();
        scene.destroyedHandlers.push({
            type: kind,
            handler: handler
        })
    }

    /**
     * Run code when two kinds of sprites overlap
     */
    //% group="Overlaps"
    //% weight=100 draggableParameters
    //% blockId=spritesoverlap block="on $sprite of kind $kind=spritetype overlaps $otherSprite of kind $otherKind=spritetype"
    //% help=sprites/on-overlap
    export function onOverlap(kind: number, otherKind: number, handler: (sprite: Sprite, otherSprite: Sprite) => void) {
        if (kind == undefined || otherKind == undefined ||!handler) return;

        const scene = game.currentScene();
        scene.overlapHandlers.push({
            type: kind,
            otherType: otherKind,
            handler: handler
        })
    }
}

namespace scene {
    /**
     * Run code when a certain kind of sprite hits a tile
     * @param direction
     * @param tile
     * @param handler
     */
    //% group="Collisions"
    //% weight=100 draggableParameters
    //% blockId=spritesollisions block="on $sprite of kind $kind=spritetype hits wall $tile=colorindexpicker"
    //% help=scene/on-hit-tile
    export function onHitTile(kind: number, tile: number, handler: (sprite: Sprite) => void) {
        if (kind == undefined || !handler) return;

        const scene = game.currentScene();
        scene.collisionHandlers.push({
            type: kind,
            tile: tile,
            handler: handler
        })
    }
}

game.spritemap.ts:
namespace sprites {
    export class SpriteMap {
        private cellWidth: number;
        private cellHeight: number;
        private rowCount: number;
        private columnCount: number;
        private buckets: Sprite[][];

        constructor() {
            this.buckets = [];
        }

        /**
         * Returns a potential list of neighbors
         */
        neighbors(sprite: Sprite): Sprite[] {
            if (this.isOob(sprite)) return [];

            const n: Sprite[] = [];
            const layer = sprite.layer;
            this.mergeAtKey(sprite.left, sprite.top, layer, n)
            this.mergeAtKey(sprite.left, sprite.bottom, layer, n)
            this.mergeAtKey(sprite.right, sprite.top, layer, n)
            this.mergeAtKey(sprite.right, sprite.bottom, layer, n)
            n.removeElement(sprite);
            return n;
        }

        /**
         * Gets the overlaping sprites if any
         * @param sprite
         */
        overlaps(sprite: Sprite): Sprite[] {
            const n = this.neighbors(sprite);
            const o = n.filter(neighbor => sprite.overlapsWith(neighbor));
            return o;
        }

        draw() {
            for (let x = 0; x < this.columnCount; ++x) {
                for (let y = 0; y < this.rowCount; ++y) {
                    const left = x * this.cellWidth;
                    const top = y * this.cellHeight;
                    const k = this.key(left, top);
                    const b = this.buckets[k];
                    if (b && b.length)
                        screen.drawRect(left, top, this.cellWidth, this.cellHeight, 5);
                }
            }
        }

        /**
         * Recompute hashes for all objects
         */
        update(sprites: Sprite[]) {
            this.buckets = [];

            // rescale buckets
            let maxWidth = 0;
            let maxHeight = 0;
            for (const sprite of sprites) {
                if (sprite.width > maxWidth) maxWidth = sprite.width;
                if (sprite.height > maxHeight) maxHeight = sprite.height;
            }

            const tMap = game.currentScene().tileMap;

            const areaWidth = tMap ? tMap.areaWidth() : screen.width;
            const areaHeight = tMap ? tMap.areaHeight() : screen.height;

            this.cellWidth = Math.clamp(8, areaWidth / 4, maxWidth * 2);
            this.cellHeight = Math.clamp(8, areaHeight / 4, maxHeight * 2);
            this.rowCount = (areaHeight / this.cellHeight) >> 0
            this.columnCount = (areaWidth / this.cellWidth) >> 0;


            for (const sprite of sprites)
                this.insertAABB(sprite);
        }

        private key(x: number, y: number): number {
            const xi = Math.clamp(0, this.columnCount, (x / this.cellWidth) >> 0);
            const yi = Math.clamp(0, this.rowCount, (y / this.cellHeight) >> 0);
            return xi + yi * this.columnCount;
        }

        private insertAtKey(x: number, y: number, sprite: Sprite) {
            const k = this.key(x, y);
            let bucket = this.buckets[k];
            if (!bucket)
                bucket = this.buckets[k] = [];
            if (bucket.indexOf(sprite) < 0)
                bucket.push(sprite);
        }

        private isOob(sprite: Sprite): boolean {
            const tMap = game.currentScene().tileMap;

            const areaWidth = tMap ? tMap.areaWidth() : screen.width;
            const areaHeight = tMap ? tMap.areaHeight() : screen.height;
            return sprite.right < 0 || sprite.left > areaWidth || sprite.bottom < 0 || sprite.top > areaHeight;
        }

        private insertAABB(sprite: Sprite) {
            // is object completely out of space?
            if (this.isOob(sprite) || (sprite.flags & sprites.Flag.Ghost))
                return;

            const left = sprite.left;
            const top = sprite.top;
            const xn = Math.ceil(sprite.width / this.cellWidth)
            const yn = Math.ceil(sprite.height / this.cellHeight);
            for (let x = 0; x <= xn; x++)
                for (let y = 0; y <= yn; y++)
                    this.insertAtKey(left + Math.min(sprite.width, x * this.cellWidth), top + Math.min(sprite.height, y * this.cellHeight), sprite)
        }

        private mergeAtKey(x: number, y: number, layer: number, n: Sprite[]) {
            const k = this.key(x, y);
            const bucket = this.buckets[k];
            if (bucket) {
                for (const sprite of bucket)
                    if ((sprite.layer & layer)
                        && n.indexOf(sprite) < 0)
                        n.push(sprite);
            }
        }

        toString() {
            return `${this.buckets.length} buckets, ${this.buckets.filter(b => !!b).length} filled`;
        }
    }
}

game.sprites.ts:
/*
Frame handlers:
 10 - physics and collisions
 20 - frame()
 60 - screen/sprite background
 90 - drawing sprites
 95 - drawing score
100 - loops.menu()
200 - screen refresh
*/

/**
 * Sprites on screen
 */
//% weight=49 color="#4B7BEC" icon="\uf1d8"
//% groups='["Create", "Properties", "Overlaps", "Collisions", "Lifecycle"]'
namespace sprites {

    /**
     * Create a new sprite from an image
     * @param img the image
     */
    //% group="Create"
    //% blockId=spritescreate block="sprite %img=screen_image_picker of kind %kind=spritetype"
    //% expandableArgumentMode=toggle
    //% blockSetVariable=mySprite
    //% weight=100 help=sprites/create
    export function create(img: Image, kind?: number): Sprite {
        const scene = game.currentScene();
        const sprite = new Sprite(img)
        sprite.type = kind;
        scene.allSprites.push(sprite)
        sprite.id = scene.allSprites.length
        scene.physicsEngine.addSprite(sprite);

        // run on created handlers
        scene.createdHandlers
            .filter(h => h.type == kind)
            .forEach(h => h.handler(sprite));

        return sprite
    }

    /**
     * Return an array of all sprites of the given kind.
     * @param kind the target kind
     */
    //% blockId=allOfKind block="array of sprites of kind %kind=spritetype"
    //% blockNamespace="arrays" blockSetVariable="sprite list"
    //% weight=87
    export function allOfKind(kind: number): Sprite[] {
        const spritesByKind = game.currentScene().spritesByKind;
        if (!(kind >= 0) || !spritesByKind[kind]) return [];
        else return spritesByKind[kind].slice(0, spritesByKind[kind].length);
    }

    /**
     * Create a new sprite with given speed, and place it at the edge of the screen so it moves towards the middle.
     * The sprite auto-destroys when it leaves the screen. You can modify position after it's created.
     */
    //% group="Create"
    //% blockId=spritescreateprojectile block="projectile %img=screen_image_picker vx %vx vy %vy of kind %kind=spritetype || from sprite %sprite=variables_get"
    //% weight=99 help=sprites/create-projectile
    //% blockSetVariable=projectile
    //% inlineInputMode=inline
    //% expandableArgumentMode=toggle
    export function createProjectile(img: Image, vx: number, vy: number, kind: number, sprite?: Sprite) {
        const s = sprites.create(img, kind);
        s.vx = vx
        s.vy = vy

        // put it at the edge of the screen so that it moves towards the middle

        if (vx < 0)
            s.x = screen.width + (s.width >> 1) - 1
        else if (vx > 0)
            s.x = -(s.width >> 1) + 1

        if (vy < 0)
            s.y = screen.height + (s.height >> 1) - 1
        else if (vy > 0)
            s.y = -(s.height >> 1) + 1

        s.flags |= sprites.Flag.AutoDestroy;

        if (sprite) {
            s.x = sprite.x;
            s.y = sprite.y;
        }

        return s
    }

    /**
     * Creates a new sprite of the given kind and adds it to the game. Use this
     * with the "on sprite created" event.
     * @param kind the kind of sprite to create
     */
    //% group="Lifecycle"
    //% blockId=spritecreateempty block="create empty sprite of kind %kind=spritetype"
    //% weight=98
    export function createEmptySprite(kind: number): void {
        sprites.create(image.create(1, 1), kind);
    }

    export enum Flag {
        Ghost = 1, // doesn't collide with other sprites
        Destroyed = 2,
        AutoDestroy = 4, // remove the sprite when no longer visible
        StayInScreen = 8, // sprite cannot move outside the camera region
    }
}

